def get_action_description(action: Any) -> str:
    """
    Returns a human readable action description based on the action type and config
    """
    action_type = action.type
    if action_type == "email":
        config = action.config or {}
        target_type = config.get("target_type")

        if target_type == 4:  # ISSUE_OWNERS
            return "Email Issue Owners"
        elif target_type == 2:  # TEAM
            target_identifier = config.get("target_identifier")
            return "Email Team" if not target_identifier else f"Email Team #{target_identifier}"
        elif target_type == 1:  # USER
            target_identifier = config.get("target_identifier")
            return "Email Member" if not target_identifier else f"Email Member #{target_identifier}"
        else:
            return "Email"

    if action_type == "sentry_app":
        config = action.config or {}
        target_display = config.get("target_display")
        if target_display:
            return f"Notify {target_display}"
        return "Sentry App"

    if action_type in ACTION_TYPE_TO_STRING:
        config = action.config or {}
        if action_type in ["slack", "discord", "msteams"]:
            target_display = config.get("target_display")
            if target_display:
                return f"{ACTION_TYPE_TO_STRING[action_type]} {target_display}"

        return ACTION_TYPE_TO_STRING[action_type]

    return action_type.replace("_", " ").title()


def generate_workflow_name_from_actions(actions: list[Any]) -> str:
    """
    Generate a workflow name based on actions like:
    'Notify: Slack #alerts, Email Issue Owners...(+3)'
    """
    if not actions:
        return "Notify: Workflow"

    name_parts = []
    action_count = 0

    for action in actions[:MAX_ACTIONS]:
        description = get_action_description(action)
        name_parts.append(description)
        action_count += 1

    if len(actions) > MAX_ACTIONS:
        remaining = len(actions) - MAX_ACTIONS
        name = ", ".join(name_parts)
        name += f"...(+{remaining})"
    else:
        name = ", ".join(name_parts)

    return f"Notify: {name}"


def update_cron_workflow_names(apps: StateApps, schema_editor: BaseDatabaseSchemaEditor) -> None:
    """
    Update workflow names for cron-linked workflows after deduplication.

    Since workflows have been deduplicated, the monitor-specific names like
    "Monitor Alert: monitor-name" no longer make sense. Update them to be
    based on the actions in the workflow.
    """
    Workflow = apps.get_model("workflow_engine", "Workflow")
    Action = apps.get_model("workflow_engine", "Action")

    cron_workflows = list(
        Workflow.objects.filter(
            detectorworkflow__detector__type="monitor_check_in_failure"
        ).distinct()
    )

    if not cron_workflows:
        logger.info("No workflows linked to cron detectors, skipping workflow name updates")
        return

    workflows_updated = 0

    for workflow in cron_workflows:
        try:
            if not workflow.name or not workflow.name.startswith("Monitor Alert:"):
                continue

            all_actions = list(
                Action.objects.filter(
                    dataconditiongroupaction__condition_group__workflowdataconditiongroup__workflow_id=workflow.id
                )
                .distinct()
                .order_by("id")
            )

            if all_actions:
                new_name = generate_workflow_name_from_actions(all_actions)
                if new_name != workflow.name:
                    workflow.name = new_name
                    workflow.save(update_fields=["name"])
                    workflows_updated += 1
        except Exception:
            logger.exception("Failed to update workflow name")

    logger.info(
        "Updated workflow names",
        extra={
            "workflows_updated": workflows_updated,
            "total_cron_workflows": len(cron_workflows),
        },
    )


